    private void tblBusMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblBusMouseClicked
        // TODO add your handling code here:
        DefaultTableModel model = (DefaultTableModel)tblBus.getModel();
        int row = tblBus.getSelectedRow();
        Bus bus = new Bus();
        
        bus = bus.getById(Integer.parseInt(model.getValueAt(row, 0).toString()));
        
        txtIdBus.setText(String.valueOf(bus.getIdbus()));
        txtNamaBus.setText(bus.getNamabus());
        txtJamBerangkat.setText(bus.getJamberangkat());
        txtHargaTiket.setText(String.valueOf((bus.getHargatiket())));
        txtTerminalAsal.setText(bus.getTerminalasal());
        txtTerminalTujuan.setText(bus.getTerminaltujuan());
    }//GEN-LAST:event_tblBusMouseClicked

    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        // TODO add your handling code here:
        Bus bus = new Bus();
        bus.setIdbus(Integer.parseInt(txtIdBus.getText()));
        bus.setNamabus(txtNamaBus.getText());
        bus.setJamberangkat(txtJamBerangkat.getText());
        bus.setHargatiket(Integer.parseInt(txtHargaTiket.getText()));
        bus.setTerminalasal(txtTerminalAsal.getText());
        bus.setTerminaltujuan(txtTerminalTujuan.getText());
        bus.save();
        
        txtIdBus.setText(Integer.toString(bus.getIdbus()));
        
        tampilkanData();
    }//GEN-LAST:event_btnSimpanActionPerformed

