    public void kosongkanForm() {
        txtIdBus.setText("0");
        txtNamaBus.setText("");
        txtJamBerangkat.setText("");
        txtHargaTiket.setText("");
        txtTerminalAsal.setText("");
        txtTerminalTujuan.setText("");
    }
    
    public void tampilkanData() {
        String[] kolom = {"ID Bus", "Nama Bus", "Jam Berangkat", "Harga Tiket", "Terminal Asal", "Terminal Tujuan"};
        ArrayList<Bus> list = new Bus().getAll();
        Object rowData[] = new Object[7];
        
        tblBus.setModel(new DefaultTableModel(new Object[][] {}, kolom));
        
        for(Bus k : list) {
            rowData[0] = k.getIdbus();
            rowData[1] = k.getNamabus();
            rowData[2] = k.getJamberangkat();
            rowData[3] = k.getHargatiket();
            rowData[4] = k.getTerminalasal();
            rowData[5] = k.getTerminaltujuan();
            
            ((DefaultTableModel)tblBus.getModel()).addRow(rowData);
        }
    }
    
    public void cari(String keyword) {
        String[] kolom = {"ID Bus", "Nama Bus", "Jam Berangkat", "Harga Tiket", "Terminal Asal", "Terminal Tujuan"};
        ArrayList<Bus> list = new Bus().search(keyword);
        Object rowData[] = new Object[7];
        
        tblBus.setModel(new DefaultTableModel(new Object[][] {}, kolom));
        
        for(Bus k : list) {
            rowData[0] = k.getIdbus();
            rowData[1] = k.getNamabus();
            rowData[2] = k.getJamberangkat();
            rowData[3] = k.getHargatiket();
            rowData[4] = k.getTerminalasal();
            rowData[5] = k.getTerminaltujuan();
            
            ((DefaultTableModel)tblBus.getModel()).addRow(rowData);
        }
    }
